import React, { useState } from 'react' 
import sd from "../assets/img/ZooLogos/SanDiego.png";
import bronx from "../assets/img/ZooLogos/bronx.png";
import oak from "../assets/img/ZooLogos/Oakland.png";
import elm from "../assets/img/ZooLogos/Elmswood.png";
import { LitElement, html, customElement, property } from "lit-element";
import { useLocation } from "react-router-dom";

import MintNFT from "./components/MintNFT";
import TokenData from './components/TokenData';
import GetIDs from './components/GetIDs';
import ViewBalance from './components/ViewBalance';
import InitializeAcct from './components/InitializeAcct';
import TransactionTest from './components/TransactionTest';
import DisplayAvatar from './components/DisplayAvatar';
import DisplayNFTGrid from './components/DisplayNFTGrid';
import ListForSale from './components/ListForSale';


class AcctPage extends React.Component {

    constructor(props) {
      super(props);
      this.state = {nftIDMeta: "1", price:"10.0"};
    }

    onInputchange(e,stateEle) {
      let idValue = document.getElementById(e).value
      this.setState({
        stateEle: idValue
      });
    }

    render() {
      return(
      <div class="container m-auto">
        <div class="row fadeIn mt-3 p-2 block">
          <h2 class="text-6xl">Account Page</h2>
          <div class="row">
            <InitializeAcct></InitializeAcct>
            <DisplayNFTGrid></DisplayNFTGrid>
            <ViewBalance></ViewBalance>
            <label for="acctNftIDMeta" class="mt-3"><b>NFT ID</b></label><br/>
                    <input
                      type="text"
                      id="acctNftIDMeta"
                      data-field="id"
                      value={this.state.nftIDMeta}
                      onChange={() => this.onInputchange("acctNftIDMeta",this.state.nftIDMeta)}/>
            <label for="acctNFTPrice" class="mt-3"><b>NFT ID</b></label><br/>
                    <input
                      type="text"
                      id="acctNFTPrice"
                      data-field="id"
                      value={this.state.acctNFTPrice}
                      onChange={() => this.onInputchange("acctNFTPrice",this.state.price)}/>
            <ListForSale></ListForSale>
            <DisplayAvatar></DisplayAvatar>
          </div>
        </div>
      </div>)
    ;
    }
}

export default AcctPage;
