import React, { useState } from "react";
import * as fcl from "@onflow/fcl";
import * as t from "@onflow/types"
import CheckProfile from './CheckProfile';

const InitializeAcct = () => {
  const [status, setStatus] = useState("Not Started")
  const Mint = async () => {
    const encoded = await fcl
      .send([
        fcl.transaction`
        import Profile from 0xba1132bc08f82fe2

        transaction {
          // We want the account's address for later so we can verify if the account was initialized properly
          let address: Address

          prepare(account: AuthAccount) {
            // save the address for the post check
            self.address = account.address

            // Only initialize the account if it hasn't already been initialized
            if (!Profile.check(self.address)) {
              // This creates and stores the profile in the user's account
              account.save(<- Profile.new(), to: Profile.privatePath)

              // This creates the public capability that lets applications read the profile's info
              account.link<&Profile.Base{Profile.Public}>(Profile.publicPath, target: Profile.privatePath)
            }
          }

          // verify that the account has been initialized
          post {
            Profile.check(self.address): "Account was not initialized"
          }
        }
      `,
      fcl.proposer(fcl.authz),
      fcl.authorizations([fcl.authz]),
      fcl.payer(fcl.authz)
    ]
    )
    setStatus("Transaction Sent, Waiting for Confirmation")
    const decoded = await fcl.decode(encoded)
    const unsub = fcl.tx(decoded).subscribe(transaction => {
      if (fcl.tx.isSealed(transaction)) {
        setStatus(decoded)
        unsub()
      }
    })
    console.log(decoded)

  };
  return (
    <div className="mint-nft">
        <div className="center">
        <div className="fancy">
        <div className="center">
            <button className="btn-primary" onClick={Mint}>InitializeAcct</button>  
            <div>
              {status}
              <CheckProfile></CheckProfile>
            </div>      
        </div>
        </div>
        </div>
    </div>
  );
};

export default InitializeAcct;
