import React, { useState } from 'react' 
import sd from "../assets/img/ZooLogos/SanDiego.png";
import bronx from "../assets/img/ZooLogos/bronx.png";
import oak from "../assets/img/ZooLogos/Oakland.png";
import elm from "../assets/img/ZooLogos/Elmswood.png";
import { LitElement, html, customElement, property } from "lit-element";
import { useLocation } from "react-router-dom";

import MintNFT from "./components/MintNFT";
import TokenData from './components/TokenData';
import GetIDs from './components/GetIDs';
import InitializeAcct from './components/InitializeAcct';
import TransactionTest from './components/TransactionTest';

class ZooPage extends React.Component {

    constructor(props) {
      super(props);
      this.state = {nftIDMeta: "1"};
    }

    // Use this to display different icons depending on the logo clicked
    renderSwitch(imgTag) {
      console.log(this.props.location.state.imgSrc)
      console.log(this.props.location.state.account)
      switch(imgTag) {
        case "sd":
          return <img 
          src={sd}
          id="viewIcon"
          alt="SDZooLogo" 
          title="SDZooLogo"> 
        </img>;
        case "bronx":
          return <img 
          src={bronx} 
          id="viewIcon"
          alt="BronxZooLogo" 
          title="BronxZooLogo"> 
        </img>;
        default:
          return 'test';
      }
    }

    onInputchange() {
      let value = document.getElementById("nftIDMeta").value
      this.setState({
        nftIDMeta: value
      });
    }

    render() {
      return(
      <div className="container m-auto">
        <div className="row fadeIn mt-3 p-2 block">
          <h2 className="text-6xl">Zoo Page</h2>
          <div className="row">
            <div className="column">
              {this.renderSwitch(this.props.location.state.imgSrc)}
              <label for="nftIDMeta" class="mt-3"><b>NFT ID</b></label><br/>
                    <input
                      type="text"
                      id="nftIDMeta"
                      data-field="id"
                      value={this.state.nftIDMeta}
                      onChange={() => this.onInputchange()}/>  
              <TokenData></TokenData>
              <GetIDs></GetIDs>
            </div>
          </div>
        </div>
      </div>)
    ;
    }
}

export default ZooPage;
