import React, { useState } from "react";
import * as fcl from "@onflow/fcl";
import * as t from "@onflow/types";

const GetIDs = () => {
  const [balanceInfo, setBalanceInfo] = useState(null)
  const fetchTokenData = async () => {
    const encoded = await fcl
      .send([
        fcl.script`
        import WildCoin from 0xProfile

        pub fun main(account: Address) : [UInt64]?{

        // Get both public account objects
        let account1 = getAccount(account)

        // Find the public Receiver capability for their Collections
        let acct1Capability = account1.getCapability(/public/NFTReceiver)

        // borrow references from the capabilities
        let receiver1Ref = acct1Capability.borrow<&{DappState.NFTReceiver}>()
            ?? panic("Could not borrow account 1 receiver reference")

        // Print both collections as arrays of IDs
        return receiver1Ref.getIDs()
        }          
      `,
      fcl.args( [fcl.arg("0xf3fcd2c1a78f5eee", t.Address)] )
      ])

    const decoded = await fcl.decode(encoded)
    setBalanceInfo(decoded)
    //console.log(nftInfo)
  };
  return (
    <div className="nft-ids">
      <div className="center">
        <button className="btn-primary" onClick={fetchTokenData}>Get Balance</button>        
      </div>
      {
        balanceInfo &&
        <div>
          {
            Object.keys(balanceInfo).map(k => {
              return (
                <p>Balance: {balanceInfo[k]}</p>
              )
            })
          }
          <button onClick={() => setBalanceInfo(null)} className="btn-secondary">Clear ID Info</button>
        </div>
      }
    </div>
  );
};

export default GetIDs;
