        {
            "name": "nft",
            "title": "NFT Collection",
            "description": "Test NFT Collection viewing page.",
            "category": "Non-fungible Tokens",
            "route": "/nft"
        }
      ]; 
    }

    handleClick = e => {
        e.preventDefault();
        console.log("E: " + e.target.dataset.link);
